var array2D = [];
for (var i = 0; i < 81; i++) {
    array2D[i] = []
    for (var j = 0; j < 86; j++) {
        array2D[i][j] = mapArray[i * 86 + j];
    }
}

var tileSize = 16;
var pwidth = 25;
var pheight = 31;
var mapWidth = 1375;
const mapHeight = 1500; 

var Entity = function(param){
    var self = {
        x:375,
        y:250,
        spdX:0,
        spdY:0,
        id:"",
        name:"",
        grid: array2D
    }

    if(param){
		if(param.x)
			self.x = param.x;
		if(param.y)
			self.y = param.y;
		if(param.id)
			self.id = param.id;		
	}

    self.isPositionWall = function(pt) {
        var gridX = Math.floor(pt.x / tileSize);
        var gridY = Math.floor(pt.y / tileSize);
        if(gridX < 0 || gridX >= self.grid[0].length) {
            return true;
        }
        if(gridY < 0 || gridY >= self.grid.length) {
            return true;
        }
        
        return self.grid[gridY][gridX];
    }
    // self.isTopWall = function (pt) {
    //     var gridY = Math.floor(pt.y / tileSize);
        
    //     if(gridY < 0 || gridY >= self.grid.length) {
    //         return true;
    //     }   
    //     return self.grid[gridY][gridX]; 
    // }


    self.update = function(){
        self.updatePosition();
    }

    self.updatePosition = function(){
        // self.x += self.spdX;
        // self.y += self.spdY;
        //player width is 50
        //player height is 63
        const oldX = self.x;
        const oldY = self.y;
        self.x +=self.spdX;
        self.y +=self.spdY;
       
        if (self.isPositionWall(self) && !self.isBullet) {
             //need to change to allow movement while sitcking to the wall
            self.x = oldX;
            self.y = oldY;
        } else if(self.isPositionWall(self) && self.isBullet) {
            self.toRemove = true;
        }

        self.getDistance = function(pt){
            return Math.sqrt(Math.pow(self.x-pt.x,2) + Math.pow(self.y-pt.y,2));
        }
        
            
        // if(!(self.x < 0) && !(self.x > 750)) {
        //     self.x += self.spdX;
        // }
        // if(!(self.y < 0) && !(self.y > 500)) {
        //     self.y += self.spdY;
        // }
    }
    return self;
    
};

var Player = (name, id) => {
    var self = Entity();
        self.id = id;
        self.name = name;
        self.pressingRight = false;
        self.pressingLeft = false;
        self.pressingUp = false;
        self.pressingDown = false;
        self.pressingAttack = false;
        self.meleeAttack = false;
        self.mouseAngle = 0;
        self.maxSpd = 3;
        self.hp = 10;
	    self.hpMax = 10;
        self.score = 0;
        self.noOfbullets = 3;
        self.shootingInterval = 0;
        self.slashingInterval = 0;
        self.slash;
        self.night = false;
        var super_update = self.update;

        self.update = function(){
            self.updateSpd();
            super_update();

            if(self.pressingAttack) {
                var d = new Date();
                var t = d.getTime();
                if(!(t - self.shootingInterval < 500)) {
                    self.shootBullet(self.mouseAngle);
                    self.shootingInterval = t;
                }                              
            }
            if (self.meleeAttack) {
                var d = new Date();
                var t = d.getTime();
                if(!(t - self.slashingInterval < 250)) {
                    self.meleeAttacks(self.mouseAngle);
                    self.slashingInterval = t;
                }
            }
        }

        self.meleeAttacks = function(angle) {
            slash = {
                x: self.x + Math.cos(angle/180*Math.PI) * 16,
                y: self.y + Math.sin(angle/180*Math.PI) * 16,
                parent: self.id,
            }
            self.slash = slash;
            for(var i in Player.list){
                var p = Player.list[i];
                if(p.getDistance(slash) < 32 && slash.parent !== p.id){
                    p.hp -= 5;
                                    
                    if(p.hp <= 0){
                        var hunter = Player.list[slash.parent];
                        if(hunter)
                            hunter.score += 1;
                        p.hp = p.hpMax;
                    }
                }
            }
        }

        self.shootBullet = function(angle){
            if (self.noOfbullets != 0) {
                Bullet({
                    parent:self.id,
                    angle:angle,
                    x:self.x,
                    y:self.y,
                });
                self.noOfbullets -= 1;
            }
        }

        self.updateSpd = function(){
            if(self.pressingRight) 
                self.spdX = self.maxSpd;
            else if(self.pressingLeft)
                self.spdX = -self.maxSpd;
            else
                self.spdX = 0;
           
            if(self.pressingUp)
                self.spdY = -self.maxSpd;
            else if(self.pressingDown)
                self.spdY = self.maxSpd;
            else
                self.spdY = 0;     
        }

        self.getInitPack = function() {
            return {
                id:self.id,
                x:self.x,
                y:self.y,	
                name:self.name,	
                hp:self.hp,
			    hpMax:self.hpMax,
                score:self.score,
                bCount:self.noOfbullets,
                slashing:self.meleeAttack,
                slash:self.slash,
                night:self.night,
            };
        }

        self.getUpdatePack = function() {
            var d = new Date();
            var t = d.getTime();
            if (Math.round((t%100000)/10000)%2 ===0){
                self.night = true;
            } else {
                self.night = false;
            }
            return {
                id:self.id,
                x:self.x,
                y:self.y,
                hp:self.hp,
                score:self.score,
                bCount:self.noOfbullets,
                slashing:self.meleeAttack,
                slash:self.slash,
                night:self.night,
            }
        }

        self.isBullet = false;

        Player.list[id] = self;
        
        initPack.player.push(self.getInitPack());
        
        return self;
}

Player.list = {};

Player.onConnect = function(socket){
    socket.broadcast.emit('addToChat', socket.name + " has joined the game!"); // code to broadcast player joining the chat
    var player = Player(socket.name, socket.id);
    socket.on('keyPress',function(data){
        if(data.inputId === 'left')
            player.pressingLeft = data.state;
        else if(data.inputId === 'right')
            player.pressingRight = data.state;
        else if(data.inputId === 'up')
            player.pressingUp = data.state;
        else if(data.inputId === 'down')
            player.pressingDown = data.state;
        else if(data.inputId === 'attack')
            player.pressingAttack = data.state;
        else if(data.inputId === 'mouseAngle')
            player.mouseAngle = data.state;
        else if(data.inputId === 'meleeAttack')
            player.meleeAttack = data.state;
    
    });      
    socket.emit('init',{
        selfId:socket.id,
        player:Player.getAllInitPack(),
        bullet:Bullet.getAllInitPack(),
    });
}

Player.getAllInitPack = function(){
    var players = [];
    for(var i in Player.list)
        players.push(Player.list[i].getInitPack());
    return players;
}

Player.onDisconnect = function(socket){
    delete Player.list[socket.id];
    removePack.player.push(socket.id);
}

Player.update = function(){
    var pack = [];
    for(var i in Player.list){
        var player = Player.list[i];
        player.update();
        pack.push(player.getUpdatePack());    
    }
    return pack;
}

var Bullet = function(param){
	var self = Entity(param);
	self.id = Math.random();
	self.angle = param.angle;
	self.spdX = Math.cos(param.angle/180*Math.PI) * 10;
	self.spdY = Math.sin(param.angle/180*Math.PI) * 10;
	self.parent = param.parent;
	
	self.timer = 0;
	self.toRemove = false;
	var super_update = self.update;
	self.update = function(){
		if(self.timer++ > 100)
			self.toRemove = true;
		super_update();
		
		for(var i in Player.list){
			var p = Player.list[i];
			if(self.getDistance(p) < 32 && self.parent !== p.id){
				p.hp -= 1;
								
				if(p.hp <= 0){
					var shooter = Player.list[self.parent];
					if(shooter)
						shooter.score += 1;
					p.hp = p.hpMax;
					// p.x = Math.random() * mapWidth;
                    // p.y = Math.random() * mapHeight;
                    // while(p.isPositionWall(p)) {
                    //     p.x = Math.random() * mapWidth;
                    //     p.y = Math.random() * mapHeight;

                    // }					
				}
				self.toRemove = true;
			}
		}
	}
	self.getInitPack = function(){
		return {
			id:self.id,
			x:self.x,
			y:self.y,
		};
	}
	self.getUpdatePack = function(){
		return {
			id:self.id,
			x:self.x,
			y:self.y,		
		};
    }
    
    self.isBullet = true;
	
	Bullet.list[self.id] = self;
	initPack.bullet.push(self.getInitPack());
	return self;
}
Bullet.list = {};

Bullet.update = function(){
	var pack = [];
	for(var i in Bullet.list){
		var bullet = Bullet.list[i];
		bullet.update();
		if(bullet.toRemove){
			delete Bullet.list[i];
			removePack.bullet.push(bullet.id);
		} else
			pack.push(bullet.getUpdatePack());		
	}
	return pack;
}

Bullet.getAllInitPack = function(){
	var bullets = [];
	for(var i in Bullet.list)
		bullets.push(Bullet.list[i].getInitPack());
	return bullets;
}

